import React, { useState } from "react";
import PropTypes from "prop-types";
import {
  Card,
  CardHeader,
  ListGroup,
  ListGroupItem,
  Row,
  Col,
  Form,
  FormGroup,
  FormInput,
  FormSelect,
  FormTextarea,
  Button
} from "shards-react";
import jwt_decode from "jwt-decode";

export default function UserAccountDetails() {
  const token = localStorage.usertoken;
  const decoded = jwt_decode(token);
  const [name, setname] = useState(decoded.name)
  const [lastname, setlastname] = useState(decoded.lname)
  const [email, setemail] = useState(decoded.email)
  const [password, setpassword] = useState()
  const [address, setaddress] = useState(decoded.address)
  const [city, setcity] = useState(decoded.city)
  return (
<Card small className="mb-4">
    <CardHeader className="border-bottom">
      <h6 className="m-0">{"ข้อมูลส่วนตัว"}</h6>
    </CardHeader>
    <ListGroup flush>
      <ListGroupItem className="p-3">
        <Row>
          <Col>
            <Form>
              <Row form>
                {/* First Name */}
                <Col md="6" className="form-group">
                  <label htmlFor="feFirstName">ชื่อ</label>
                  <FormInput
                    id="feFirstName"
                    placeholder="First Name"
                    value={name}
                    onChange={(e) => {setname(e.target.value)}}
                  />
                </Col>
                {/* Last Name */}
                <Col md="6" className="form-group">
                  <label htmlFor="feLastName">นามสกุล</label>
                  <FormInput
                    id="feLastName"
                    placeholder="Last Name"
                    value={lastname}
                    onChange={(e) => {}}
                  />
                </Col>
              </Row>
              <Row form>
                {/* Email */}
                <Col md="6" className="form-group">
                  <label htmlFor="feEmail">อีเมล</label>
                  <FormInput
                    type="email"
                    id="feEmail"
                    placeholder="Email Address"
                    value={email}
                    onChange={() => {}}
                    autoComplete="email"
                  />
                </Col>
                {/* Password */}
                <Col md="6" className="form-group">
                  <label htmlFor="fePassword">รหัสผ่าน</label>
                  <FormInput
                    type="password"
                    id="fePassword"
                    placeholder="Password"
                    value={password}
                    onChange={() => {}}
                    autoComplete="current-password"
                  />
                </Col>
              </Row>
              <FormGroup>
                <label htmlFor="feAddress">ที่อยู่</label>
                <FormInput
                  id="feAddress"
                  placeholder="Address"
                  value={address}
                  onChange={() => {}}
                />
              </FormGroup>
              <Row form>
                {/* City */}
                <Col md="6" className="form-group">
                  <label htmlFor="feCity">จังหวัด</label>
                  <FormInput
                    id="feCity"
                    placeholder="City"
                    value={city}
                    onChange={() => {}}
                  />
                </Col>
                {/* State */}
                {/* <Col md="4" className="form-group">
                  <label htmlFor="feInputState"></label>
                  <FormSelect id="feInputState">
                    <option>Choose...</option>
                    <option>...</option>
                  </FormSelect>
                </Col> */}
                {/* Zip Code */}
                {/* <Col md="2" className="form-group">
                  <label htmlFor="feZipCode">Zip</label>
                  <FormInput
                    id="feZipCode"
                    placeholder="Zip"
                    onChange={() => {}}
                  />
                </Col> */}
              </Row>
              <Row form>
                {/* Description */}
                {/* <Col md="12" className="form-group">
                  <label htmlFor="feDescription">Description</label>
                  <FormTextarea id="feDescription" rows="5" />
                </Col> */}
              </Row>
              <Button theme="accent">Update Account</Button>
            </Form>
          </Col>
        </Row>
      </ListGroupItem>
    </ListGroup>
  </Card>
  )
}
